#!/bin/bash

hilla_version=${1}

if [[ -z "${hilla_version}" ]]; then
    echo "Usage: $0 <hilla major.minor version>"
    echo
    echo "Example: $0 2.5"
    echo
    exit 1
fi

echo "URL to use with a URL shortener service like https://tinyurl.com/ to create a Hilla badge"
echo "until Hilla gets added to https://simpleicons.org/"
echo
echo "Blue badge"
echo "=========="
echo "Suggested URL alias: quarkus-hilla-h-${hilla_version/./-}-b"
echo "https://img.shields.io/badge/HILLA-v${hilla_version}-blue?style=for-the-badge&logo=data:image/svg%2bxml;base64,"

echo
echo
echo
echo

echo "Green badge"
echo "=========="
echo "Suggested URL alias: quarkus-hilla-h-${hilla_version/./-}-g"
echo "https://img.shields.io/badge/HILLA-v${hilla_version}-green?style=for-the-badge&logo=data:image/svg%2bxml;base64,"
